function SearchMarker(n,i,o){var t=maui.gmap.map,e=new google.maps.Marker(i);google.maps.event.addListener(e,"click",function(){if(info_window&&info_window.close(),this.title===last_stop)return last_stop="",void 0;last_stop=this.title,info_window=new google.maps.InfoWindow({size:new google.maps.Size(150,50)}),info_window.setContent(o),info_window.open(t,e);var i=document.getElementById("destination"),s=document.getElementById("origin");jUtils.addEvent(jUtils.findByClass("destination"),"click",function(o){i.value=n,jUtils.fireEvent(i,"change"),info_window.close(),o.stopPropagation()}),jUtils.addEvent(jUtils.findByClass("origin"),"click",function(i){s.value=n,jUtils.fireEvent(s,"change"),info_window.close(),i.stopPropagation()})})}var info_window=null,last_stop="";